    int minInsertions(string s) {
        int open=0,close=0;
        int ans=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='(') open++;
            else if(s[i]==')')
            {
                if(i+1<s.size() and s[i+1]==')')
                {
                    if(open>0) open--;  // it become cancel with each other.
                    else
                        ans++;  // for one open bracket
                    i++;
                }
                else
                {
                    // Example -> (  ) , if open is there then need only for one close bracket
                    if(open>0)
                    {
                        open--;
                        ans++;
                    }
                    else
                        ans+=2;  //Example -> ), it means need 1 for open & 1 for close bracket.
                }
            }

        }

        ans+=open*2; // it means at the if some ((( left then we need 2 close for each open
        return ans;
    }
};
