class Solution {
public:
    int minInsertions(string s) {
        int res = 0, open = 0, n = s.size(), i = 0; 

        while(i<n){
            if(s[i] == '(') open ++, i++; 
            else if(open>0){
                if(i == n-1|| s[i+1] != ')') res ++, open --, i++; 
                else open --, i+=2;
            }
            else {
                open ++, res++; 
            }
        };
        res += 2*open; 
        return res; 
    }
};
