class Solution(object):
    def minInsertions(self, s):
        """
        :type s: str
        :rtype: int
        """
        open_cnt = 0
        insert = 0

        i = 0
        while i < len(s):
            char = s[i]

            if char == '(':
                open_cnt += 1
                i += 1
            else:
                if open_cnt > 0:
                    open_cnt -= 1
                else:
                    insert += 1

                if i + 1 < len(s) and s[i + 1] == ')':
                    i += 2
                else:
                    insert += 1
                    i += 1

        return insert + 2*open_cnt

