class Solution {
    public int minInsertions(String s) {
        int n = s.length();
        Stack<Character> stack = new Stack<>();
        int count = 0, curr = 0;
        for(int i = 0; i < n; i++) {
            char ch = s.charAt(i);
            if(ch == '(') {
                if(curr == 1) {
                    count++;
                    if(stack.size() > 0) stack.pop();
                    else count++;
                    curr = 0;
                }
                stack.push(ch);
            } else {
                if(curr == 0) {
                    if(stack.size() == 0) {
                        stack.push('(');
                        count++;
                    }
                    curr = 1;
                } else {
                    if(stack.size() == 0) count++;
                    else stack.pop();
                    curr = 0;
                }
            }
        }
        while(stack.size() > 0) {
            if(curr == 1) {
                curr = 0;
                count++;
            } else count += 2;
            stack.pop();
        }
        return count;
    }
}

