            // Case 2: All even, except one odd
            for (int digitWithOddOccurrences = 0; digitWithOddOccurrences < 10; ++digitWithOddOccurrences) {
                ans = Math.max(ans, i - pos.getOrDefault(currentState ^ (1 << digitWithOddOccurrences), i));
            }
            if (!pos.containsKey(currentState)) {
                pos.put(currentState, i);
            }
        }

        return ans;
    }
}
