from collections import Counter

def palindromable(s):
    # to be palindromable there needs to be an even number of all chars except one odd is acceptable
    counts = Counter(s)
    num_odds = 0
    for char, ct in counts.items():
        if ct % 2 != 0:
            num_odds += 1
            if num_odds > 1:
                return False
    return True

def findlength(s: str) -> int:
	# traverse from longest contiguous substrings
	num_chars = len(s)
	for num_dropped in range(num_chars):
		for left_idx in range(num_dropped + 1):
			right_idx = num_chars - (num_dropped - left_idx)
			sub_s = s[left_idx: right_idx]
			if palindromable(sub_s):
				output = num_chars - num_dropped
				return output
	```

