          str.setCharAt(j, '0');
        } else {
          str.setCharAt(j, '0');
          if (map.containsKey(str.toString())) {
            best = Math.max(best, i - map.get(str.toString()));
          }
          str.setCharAt(j, '1');
        }

      }
