	def longestAwesome(s: String): Int = {
        var bits = 0
        var res = 1
        val bitMap = scala.collection.mutable.Map[Int,Int]()//mask -> index
        bitMap(bits) = -1//As we need to consider the first element as well, so start index = -1 to get substring length

        s.zipWithIndex.foreach{case(c,i) =>
            bits = flipBit(bits,c.asDigit)

            //If exact same bit mask occured at an earlier index, we update result
            if(bitMap.contains(bits)){
                res = res.max(i-bitMap(bits))
            }
            //If bitMask with only 1 bit difference occured earlier (palindrome with odd length), we again update result
            for(n<- 0 to 9){
                val testBits =  flipBit(bits,n)
                if(bitMap.contains(testBits)){
                    res = res.max(i-bitMap(testBits))
                }
            }
            //We need to save the earlier position to maintain the longest path in b/w and not update the mask index everytime
            if(!bitMap.contains(bits)){
                bitMap(bits) = i
            }
        }
        res
    }

    def flipBit(n:Int,i:Int):Int = {
        var p = 1<<i
        n^p
    }
