func longestAwesome(s string) int {
    n := 0
    m := map[int]int {0: -1}
    maxLen := 1

    for i:=0;i<len(s);i++ {
        n = n ^ (1 << (s[i]-'0'))

        if val, ok := m[n]; ok {
