	public int longestAwesome(String s) {
		Map<Integer, Integer> map = new HashMap<>();
		map.put(0, -1);
		int res = 1;
		int mask = 0;
		for (int i = 0; i < s.length(); i++) {
			mask ^= 1 << (s.charAt(i) - '0');
			List<Integer> possiblePrefix = generateValidPrefix(mask);
			for (int prefix : possiblePrefix) {
				if (map.containsKey(prefix)) {
					res = Math.max(res, i - map.get(prefix));
				}
			}
			if (!map.containsKey(mask)) {
				map.put(mask, i);
			}
		}
		return res;
    }

    private List<Integer> generateValidPrefix(int mask) {
        List<Integer> prefixes = new ArrayList<>();
        prefixes.add(mask);
        for (int i = 0; i < 10; i++) {
            prefixes.add(1 << i ^ mask);
        }
        return prefixes;
    }
