class Solution {
    int max = 1;
    Map<String, Integer> memo = new HashMap<>();
    public int longestAwesome(String s) {
        return choice(new StringBuilder(s), 0, s.length());
    }

    private int choice(StringBuilder s, int i, int j) {
        if (j - i < max) return j - i;
        if (i >= j) return max;
        if (memo.containsKey(s.substring(i, j))) {
            return Math.max(max, memo.get(s.substring(i, j)));
        }
        if (isPalindrome(s, i, j)) {
            max = Math.max(max, j - i);
            return max;
        }
        int m = choice(s, i + 1, j - 1);
        memo.put(s.substring(i + 1, j - 1), m);
        int l = choice(s, i, j - 1);
        memo.put(s.substring(i, j - 1), l);
        int r = choice(s, i + 1, j);
        memo.put(s.substring(i + 1, j), r);
        return Math.max(m, Math.max(l, r));
    }

    Map<String, Boolean> memo2 = new HashMap<>();
    private boolean isPalindrome(StringBuilder s, int i, int j) {
        String key = i + "_" + j + "_" + s;
        if (memo2.containsKey(key)) return memo2.containsKey(key);
        Map<Character, Integer> map = new HashMap<>();
        for(int k = i; k < j; k++) {
            char c = s.charAt(k);
            map.put(c, map.getOrDefault(c, 0) + 1);
        }
        int odd = 0;
        for(char c : map.keySet()) {
            if (map.get(c) % 2 != 0) odd++;
        }
        if (odd > 1) {
            memo2.put(key, false);
            return false;
        }
        memo2.put(key, true);
        return true;
    }
}
