class Solution(object):
    def longestAwesome(self, s):
        """
        :type s: str
        :rtype: int
        """
        dp = collections.defaultdict(int)
        dp[0] = -1

        ans = 0
        curr = 0
        for i,c in enumerate(s):
            curr ^= (1 << int(c))

            if not curr:
                ans = max(ans,i-dp[curr])
            else:
                for j in reversed(range(10)):
                    if (curr ^ (1 << j)) in dp:
                        ans = max(ans,i-dp[(curr ^ (1 << j))])

            if curr not in dp:
                dp[curr] = i

        return ans
