class Solution {

    unordered_map<string,int> cache;

    bool isMapPalindrome(unordered_map<char,int>& myMap) {
        //one odd or no odd
        int oddCount = 0;

        for(auto& m: myMap) {
            if(m.second % 2 == 1)
                oddCount++;
        }

        if(oddCount <= 1)
            return true;
        else
            return false;
    }

public:
    int longestAwesome(string s) {
        unordered_map<char,int> myMap;

        if(cache.find(s) != cache.end())
            return cache[s];

        for(auto& ch: s)
            myMap[ch]++;

        if(isMapPalindrome(myMap)) {

            cache[s] = s.length();

            return s.length();
        }

        string sleft =  s.substr(0, s.size() - 1);
        string sright = s.substr(1, s.size() - 1);
        string middle = s.substr(1, s.size() - 2);


        cache[s] = max(max(longestAwesome(sleft), longestAwesome(sright)),longestAwesome(middle));
        return cache[s];
    }
};
