class Solution {
public:
    string makeGood(string s) {
        int n=s.length()-1,i=0;
        stack<char> chk;
        string ans="";
        for(int i=0;i<=n;i++)
        {
            if(chk.empty())
            {
                chk.push(s[i]);
            }
            else{  
                   if(s[i]>=97&&s[i]<=122)
                   {
                       if(s[i]-chk.top()==32)
                       chk.pop();
                       else{chk.push(s[i]);}
                   }
                   else if(s[i]>=65&&s[i]<=90)
                   {
                       if(chk.top()-s[i]==32)
                       chk.pop();
                       else{chk.push(s[i]);}
                   }

            }
        }
        while(!chk.empty())
        {
        ans.push_back(chk.top());
        chk.pop();
        }
        reverse(ans.begin(),ans.end());
        return ans;

    }
};
```

