class Solution {
    public String makeGood(String s) {
        StringBuilder sb = new StringBuilder(s);
        for(int i=0; i<sb.length()-1; i++)
        {
		// Checking if two adjacent characters are in upper and lower case. 
		//If both are same then their ASCII difference will always be 32 (For Ex - A->65 and a-> 97 therefore |65-97| = 32) 
            if(Math.abs((int)sb.charAt(i) - (int)sb.charAt(i+1)) == 32)  
            {
                sb.deleteCharAt(i);
                sb.deleteCharAt(i);
                if(i == 0)
                    --i;
                else
                    i = i-2;
            }
            if(sb.length() == 0)
                return "";
        }
        return sb.toString();
    }
}
