class Solution {
    public String makeGood(String s) {
        StringBuilder sb=new StringBuilder();
        Stack<Character> st=new Stack<>();
        for(int i=0;i<s.length();i++){
            char ch=s.charAt(i);
            if(st.size()>0){
                if(st.peek()-ch==32 || st.peek()-ch==-32){
                    st.pop();
                }else{
                st.push(ch);
                }
            }else{
                st.push(ch);
            }
        }
