class Solution {
public:
    bool canConvertString(string s, string t, int k) {

        if(s.size() != t.size()) return false;

        int n = s.size();
        vector<int> v(n,0);
        map<int,int> mp;

        for(int i = 0;i < n;i++){
            int x = t[i] - s[i];
            if(x < 0) x = 26 + x;   // s[i] > t[i] then we have to roll back
            if(x != 0)
                v[i] = mp[x]*26 + x;    //if same x comes then we can roll for another 26 times if we have moves
            else v[i] = x;
            if(v[i] > k) return false;  // if moves are more then limit then return false
            mp[x]++;
        }

        return true;
    }
};
```

