public boolean canConvertString(String s, String t, int k) {

    int len1 = s.length(), len2 = t.length();
    if(len1 != len2)
        return false;

    int[] next = new int[26];

    for(int i=0;i<26;i++)
        next[i] = i;

    for(int i = 0 ; i < len1 ; i++){

        int diff = t.charAt(i) - s.charAt(i);
        if(diff == 0)
            continue;

        if(diff < 0)
            diff += 26;

        int move = next[diff];
        next[diff] += 26;

        if(move > k)
            return false;
    }

    return true;
}

