class Solution(object):
	def canConvertString(self, s, t, k):
		if s==t:
			return True
		if len(s)!=len(t):
			return False

		shift_require_dict = collections.defaultdict(int) 
		'''Dict Storing Difference/shift as key and value as frequency 
		of that shift require to convert all char with same shift/difference 
		to respective char in 't'. Example s=abc t=bcd , we require 1 shift three times.So key=1 val=3'''
		for i,j in zip(s,t):
			if i!=j:
				if j<i:
					a = (122-ord(i)) + (ord(j)-96) # if char[s] > char[t], then we may go till z and again come char[t]
					shift_require_dict[a] +=1
				else:
					a = ord(j)-ord(i)
					shift_require_dict[a] += 1

'''Now for a particular key, we require maximum
26*(val-1)+key number if we want to convert all 
char with same shift(key) to valid char in 't'.
Example s = abc t =bcd we require number 1,27,53 to convert s to t.'''

# So MAX_Val required will be max_freq with largest shift
		max_f = shift_require_dict[max(shift_require_dict,key=shift_require_dict.get)]
		max_diff =0
		for key,val in shift_require_dict.items():
			if val ==max_f:
				max_diff = max(max_diff,key)

		if (26*(max_f-1) + max_diff) <=k:
			return True
		else:
			return False

