            if(s[i]<t[i]){
                req=t[i]-s[i];
            }else{
                req=t[i]-'a'+('z'-s[i])+1;
            }
            if(req>k) return false;
            mp[req]++;
            int max=k/26;
            if(req<=left) max++;
            if(mp[req]>max) return false;
        }
        return true;
