bool canConvertString(string s, string t, int k)
{
	int n = s.length();
	int l = t.length();

	if (n != l)
	{
		return false;
	}
	else
	{
		map<int, int> m;
		int diff = 0;
		for (int i = 0; i < n; i++)
		{
			if (s[i] != t[i])
			{
				diff = t[i] - s[i];
				if (diff < 0)
				{
					diff = 26 + diff;
				}
			}
			else
			{
				continue;
			}
			if (diff <= k)
			{
				if (m.find(diff) == m.end())
