class Solution {
public:
    bool canConvertString(string s, string t, int k) {
        int sl = s.length(), tl = t.length();
        if(sl != tl) return false;

        vector<int> m(27, k/26);
        for(int i = 1; i < 27; i++) {
            if(k%26 >= i) m[i]++;
