class Solution {
public:
    // calculates distance between char a and b through shifting right
    int dist(char a, char b){
        int moves = 0;
        while (a != b){
            ++moves;
            if (a == 'z') a = 'a';
            else ++a;
        }
        return moves;
    }
    bool canConvertString(string s, string t, int k) {
        if (s.size() != t.size()) return false;
        unordered_map<int,int> m;
        int remainder = k % 26;
        for (int i = 1; i <= 26; ++i){
            m[i] += k / 26;
            if (remainder-- > 0) m[i] += 1;
        }
        for (int i = 0; i < s.size(); ++i){
            if (s[i] != t[i]){
                int d = dist(s[i], t[i]);
                if (--m[d] == -1) return false;
            }
        }
        return true;
    }
};
