public class Solution {
    public bool CanConvertString(string s, string t, int k) {

        if(s.Length!=t.Length) return false;
        var map = new int[27];

        var whole = k/26;
        var partial = k%26;

        //Fill values with available replaces
        // a => b can happen with 1 or 27 so we can count 27 as 1
        for(int i=1; i<=26; i++)
        {
            map[i] = whole;
            if(i<=partial) map[i]++;
        }

        for(int i=0; i<s.Length; i++)
        {
            //(t[i] - s[i]) =>  a->z. => 26-1 => 25
            //(t[i] - s[i] + 26) => z -> a  => 1 - 26 + 26 => 1
            var diff = (t[i] - s[i]) >= 0 ? (t[i] - s[i]) : (t[i] - s[i] + 26);

            //If difference is 0 then we don't need to do anything
            if(diff== 0) continue;

            //If we don't have any replace available
            if(map[diff] <= 0) return false;
            map[diff]--;

        }

        return true;
    }
}
