class Solution {
public:
    bool canConvertString(string s, string t, int k) {
        unordered_map<int,int> m;
        if(s.length()!=t.length())
            return(false);
        int q=k/26,o=k%26;
        for(int l=0;l<=25;l++)
        {
            if(l<=o)
                m[l]++;
            m[l]+=q;
        }
        for(int i=0;i<s.length();i++)
        {
            int p=t[i]-s[i];
            if(p<0)
