class Solution {
public:
    bool canConvertString(string s, string t, int k) {
        if(s.length()!=t.length())return false;
        unordered_map<int,int> vis;
        priority_queue<long long> que;
        for(int i=0;i<s.length();i++){
            int shft=t[i]-s[i];
            if(shft==0)continue;
            shft=shft<0?shft+26:shft;
            long long x=shft+26*vis[shft]; //is same rotn is there we have to move it to next cycle
            vis[shft]++;
            que.push(x);
        }
        if(!que.empty()&&que.top()>k)return false;
        return true;

    }
};
