public class Solution {
    public bool CanConvertString(string s, string t, int k) {
        if(s.Length != t.Length) return false;
        int times = k/26;
        k %= 26;
        Dictionary<int, int> dict = new Dictionary<int, int>();
        for(int i=0; i<s.Length; i++)
        {
            if(s[i] == t[i]) continue;
            int shift = s[i]-t[i]>0? 26-s[i]+t[i] : t[i]-s[i];
            if(dict.ContainsKey(shift))
            {
                dict[shift]++;
            }
            else
            {
                dict.Add(shift, 1);
            }

            if(dict[shift] > times)
            {
                if(shift > k || dict[shift] - times > 1)
                    return false;
            }
        }
        return true;
    }
}
