    int minInsertions(string s) {
        stack<char> v;
        int ans = 0;
        for(int i = 0;i < s.size();i++){
            if(s[i] == '(') v.push(s[i]);
            else{
                if(s[i] == ')' && i < s.size() && s[i + 1] == ')') {
                    if(!v.empty())
                        v.pop();
                    else ans++;
                    i++;       // because we considered i+1 in this case
