class Solution {
public:
    int minInsertions(string s) {
        int n = s.length();

        int minInsert = 0; // final answer 

        vector<char> ss; // is the equivalent string wherein '))' is denoted by a single ')'


       //1. Find the number of inserts to make consecutive '))'---
        for(int i=0;i<n;i++){
            ss.push_back(s[i]);

            if(s[i] == ')'){
                if(i == n-1 || s[i+1] != ')')
                    minInsert++;
                else
                    i++;
            }
        }
        //---------------------------------------------------------


        //2. Now, just usual minimum inserts to balance where 'ss' is the new string we will use
        n = ss.size();


        //2.1: left-to-right -> to balance unpaired ')' 
        int balance = 0;
        for(int i=0;i<n;i++){
            balance += (ss[i]=='(') - (ss[i]==')');
            if(balance == -1){
                minInsert+=1; // we need 1 extra '(' to balance one unpaired ')' {wherein ')' == '))' as per problem}
                balance= 0;
            }
        }


        //2.2: right-to-left -> to balance unpaired '('
        balance = 0;
        for(int i=n-1;i>=0;i--){
            balance += (ss[i]==')') - (ss[i]=='(');
            if(balance == -1){
                minInsert+=2; // NOTE: we need '))' to balance one unpaired '(', hence +=2
                balance = 0;
            }
        }


        return minInsert; 

    }
