class Solution:
    def minInsertions(self, s: str) -> int:
        stack = []
        cur_closed = 0
        modifications_needed = 0

        for char in s:
            if char == '(':
                if cur_closed == 1:
                    modifications_needed += 1
                    cur_closed = 0
                    stack.pop()

                stack.append('(')

