class Solution:
    def minInsertions(self, s: str) -> int:
        # https://leetcode.com/problems/minimum-insertions-to-balance-a-parentheses-string/discuss/779928/Simple-O(n)-stack-solution-with-detailed-explanation

        stack = []
        pair = 0
        for c in s:
            if c == "(": 
                if stack and stack[-1] == 1: pair += stack.pop()
                stack.append(2)

            if c == ")":
                if not stack: 
                    stack.append(1)
                    pair += 1
                elif stack[-1] == 1: stack.pop()
                elif stack[-1] == 2: stack[-1] -= 1


        return pair + sum(stack)


