int minInsertions(string s) {
	int res = 0;
	int nopen = 0; // unmatched opens

	for (int i = 0; i < s.size(); i++){
		if (s[i] == '(') {
			nopen++;
		} else {
			// If there is unmatched open use it.
			// Otherwise, insert an open parenthesis.
			if (nopen > 0) {
				nopen--;
			} else {
				res++;
			}

			// If we don't have 2 closing parenthesis, insert 
			// one closing parenthesis.
			if (i+1 < s.size() && s[i+1] == ')') {
				i++;
			} else {
				res++;
			}
		}
	}
	// Every unmatched open will require 2 closing parenthesis
	res += nopen*2;
	return res;
}
