class Solution:
    def minInsertions(self, s: str) -> int:
        res = 0
        cnt = 0  #  num of '('
        for c in s:
            if c == '(':
                cnt += 2
                if cnt&1:      # only a previous single ')' can cause cnt to be odd
                    cnt -= 1
                    res += 1
            elif c == ')':
                if cnt == 0:
                    cnt += 1
                    res += 1
                else:
                    cnt -= 1

        return res + cnt
