class Solution:
    def minInsertions(self, s: str) -> int:

        insertion = 0
        left = 0 # track imbalanced opens
        right = False # track if there is a single closing bracket
        for v in s:
            if v == '(':
                # we've closed a single bracket, so either have () or just ), inserting 1 or 2 respectively
                if right:
                    right = False
                    if left:
                        insertion += 1
                        left -= 1
                    else:
                        insertion += 2
                left += 1
            else:
                # flip whether we have a single trailing ) and evaluate insertions needed if we have a new ))
                if not right:
                    right = True
                else:
                    right = False
                    if left:
                        left -= 1
                    else:
                        insertion += 1

        if left:
            return insertion + 2  * left - right

        if right:
            return insertion + 2

        return insertion
