class Solution {
public:
    int minInsertions(string s) {
        string modified = "";
        int count = 0;
        int n = s.length();
        int i = 0;
        while(i<n)
        {
            if(s[i]=='(')
            {
                modified.push_back('(');
                i++;
            }
            else
            {
                if(i+1<n && s[i+1]==')')
                {
                    modified.push_back(')');
                    i = i+2;
                }
                else
                {
                    modified.push_back(')');
                    count++;
                    i = i+1;
                }
            }
        }
        priority_queue<int> st;
        for(int j=0;j<modified.length();j++)
        {
            if(modified[j]=='(')
            {
                st.push(j);
            }
            else
            {
               if(!st.empty())
               {
                   st.pop();
               }
               else
               {
                   count++;
               }
            }
        }

        if(st.size()>0)
        count+=2*st.size();

        return  count;
    }
};
