from collections import deque

class Solution:
    def minInsertions(self, s: str) -> int:
        stack = deque()
        insertion_count = 0

        for ch in s:
            if(ch == "("):
                if(stack and stack[-1] != "("):
                    insertion_count += 1
                    stack.pop()
                    stack.pop()
                stack.append(ch)
            else:
                if(not stack):
                    insertion_count += 1
                    stack.append("(")
                    stack.append(ch)
                else:
                    if(stack[-1] == "("):
                        stack.append(ch)
                    else:
                        stack.pop()
                        stack.pop()

        while(stack):
            if(stack[-1] == "("):
                insertion_count += 2
                stack.pop()
            else:
                insertion_count += 1
                stack.pop()
                stack.pop()

        return insertion_count
