//stack : )(
    int minInsertions(string s) {
        stack<char>st;
        int count=0;
        for(int i=0;i<s.length();i++)
        {
         if(s[i]=='(')
         {
             if(st.empty())
             {
                 st.push(s[i]);
             }
             else{
                 if(st.top()=='(')
                 {
                     st.push(s[i]);
                 }
                 else{
                    count++;
                    st.pop();
                    st.pop();
                    st.push(s[i]);
                 }
             }
         }
         else{
             if(st.empty())
             {  
                 count++;
                 st.push('(');
                 st.push(s[i]);
             }
             else
             {
                 if(st.top()=='('){
               st.push(s[i]);
                 }
                 else{
                   st.pop();
                   st.pop();
                 }
             }
         }
        }

        if(st.empty())return count;
        else {
            while(!st.empty()){
                char temp=st.top();
                st.pop();
                if(temp=='(')
                {
                    count=count+2;

                }
                else{
                    count++;
                    st.pop();
                }
            }
            return count;
        }

    }
};
```

