class Solution:
    def minInsertions(self, s: str) -> int:
        s1=s+"1"
        i=0
        l=[]
        count=0
        while(i<len(s1)):
            if(s1[i]=='('):
                l.append(s[i])
            else:
                if(s1[i]==")" and s1[i+1]==")"):
                    if(len(l)!=0):
                        l.pop()
                    else:
                        count+=1
                    i+=1
                elif(s1[i]==')' and s1[i+1]!=")"):
                    if(len(l)!=0):
                        l.pop()
                        count+=1
                    else:
                        count+=2
            i+=1
        if(len(l)!=0):
            count+=2*len(l)
        return count
