class Solution {
public:
    int minInsertions(string s) {
        stack<char> pstack;
        const char * p = s.c_str();
        int res = 0;
        while(*p != '\0') {
            //cout <<" current ch " << *p << endl;
            const char ch = *p;
            if(ch == '(') {
                pstack.push(ch);
                ++p;
            } else {
                if( *(p+1) != '\0' && *(p+1) == ')') {
                    // found "))", see if there's march in the stack
                    if(!pstack.empty()) {
                        pstack.pop();
                    } else {
                        res += 1;
                    }
                    p += 2;
                } else if(*(p+1) != '\0' && *(p+1) == '(') {
                    // only one ), need aonther one to march
                    if(!pstack.empty()) {
                        pstack.pop();
                        res += 1;
                    } else {
                        // only has ')' and no '(' in statck, need two more to be balanced
                        res += 2;
                    }
                    ++p;
                } else {
                    // p is ')' *(p+1) is '\0'
                    if(!pstack.empty()) {
                        pstack.pop();
                        res += 1;
                    } else {
                        res += 2;
                    }
                    //cout << " else ch " << ch << " next ch " << *(p+1) <<endl;
                    ++p;
                }
            }
        }

        while(!pstack.empty()){
            res += 2;
            pstack.pop();
        }

        return res;
    }
};
