class Solution {
public:
    int longestAwesome(string s) {
        int n = s.size();
        unordered_map<int,int> m;
        int mask = 0;
        m[mask] = -1;
        int ans = INT_MIN;
        for(int i=0;i<n;i++)
        {
            mask^=(1<<(s[i]-'0'));
            if(m.find(mask)!=m.end())
            {
                ans = max(ans,i-m[mask]);
            }
            for(int j=0;j<=9;j++)
            {
                int tmp = mask^(1<<j);
                if(m.find(tmp)!=m.end())
                ans = max(ans,i-m[tmp]);
            }
            if(m.find(mask)==m.end())
            m[mask] = i;
        }
        if(ans==INT_MIN)
        return 1;
        return ans;
    }
};
