class Solution {
//find string having even parity for each character + 
//string having at most one odd parity

//find longest length of such string so we store smallest index for mask in dp[mask];

public:
    int longestAwesome(string s) {
        int res = 0, mask = 0;
        int n = s.length();
        //dp[i] = min index for the prefix having parity corrosponding to i(mask);
        vector<int> dp(1024, 200000);
        dp[0] = -1;
        for(int i=0;i<n; i++){
            mask = mask ^ (1<<(s[i]-'0'));
            //check for the mask containig even no. of items;
            res = max(res, i-dp[mask]);
            //cehck for the mask containing all  even no. of items except one;
            for(int j=0; j<=9; j++){
                int newmask = mask^(1<<j);
                res = max(res, i-dp[newmask]);
            }
            dp[mask] = min(dp[mask], i);
        }
        return res;
    }
};
```

