class Solution {
public:
    int longestAwesome(string s) {
        vector<int> D(1<<11,-2), F(10, 0);
        D[0] = -1;
        int res = 1;
        auto make_mask = [&]() {
            int mask = 0;
            for (int j = 0, b = 1; j <= 9; ++j, b<<=1) {
                if (F[j]&1) mask += b;
            }
            return mask;
        };

        for (int i = 0; i < s.size(); ++i) {
            ++F[s[i]-'0'];
            int mask = make_mask();
            for (int b = 1, j = 0; j <= 9; ++j, b <<= 1) {
                if (F[j] <= 0) continue;
                mask ^= b;
                if (D[mask] >= -1) res = max(res, i-D[mask]);
                mask ^= b;
            }
            if (D[mask] >= -1) res = max(res, i-D[mask]);
            else D[mask] = i;
        }
        return res;
    }
};
