class Solution {

    public int longestAwesome(String s) {

        // We only need to care about if a digit appears odd or even number of times.
        // Since the total number of digits is 10, the number of possible states is
        // (1<<10) == 1024
        int[] idxFirstAppearance = new int[1<<10]; // index where a state first appears
        Arrays.fill(idxFirstAppearance, Integer.MIN_VALUE);
        idxFirstAppearance[0] = -1;

        int mask = 0; // initial state
        int ans = 0;

        for (int i = 0; i < s.length(); i++) {

            int idx = s.charAt(i) - '0';

            // Case 1: all digits appear even number of times
            mask ^= (1 << idx); // update state

            if (idxFirstAppearance[mask] == Integer.MIN_VALUE)
                idxFirstAppearance[mask] = i;
            else
                ans = Math.max(ans, i-idxFirstAppearance[mask]);

            // Case 2: all digits but one appear even number of times
            for (int j=0; j<10; j++) {

                int temp = mask ^ (1 << j);

                ans = Math.max(ans, i-idxFirstAppearance[temp]);
            }
        }

        return ans;
    }

}
