class Solution {
public:
    int longestAwesome(string s) {
        unordered_map<int, int>where;
        int cur=0;
        where[cur] = -1;
        int n=s.size();
        int ans=0;
        for(int i=0; i<n; i++){
            int dig = s[i]-'0';
            cur^=(1<<dig); //flip 'dig' bit

            if(where.find(cur)!=where.end())
                ans = max(ans, i-where[cur]); //all even characters

            for(int bit = 0; bit<10; bit++){
                int better = cur^(1<<bit); //allowing the 'bit' bit to be odd in the substring
                if(where.find(better)!=where.end())
                    ans = max(ans, i-where[better]); //one odd, others even
            }

            if(where.find(cur)==where.end())
                where[cur]=i;
        }
        return ans;
    }
};
