			int mask = 1 << (s[i] - '0');
			curr ^= mask;

			/// case : 1
			/// To check for all even position
			if(mp.count(curr)){
			   len = max(len , i - mp[curr]);
			}

			/// case : 2
			/// To make exactly one odd position
			for(int j = 0 ; j <= 9 ; j++){
				mask = 1 << j;
				if(mp.count(curr ^ mask)){
				   len = max(len , i - mp[curr ^ mask]);
				}
			}

			if(mp.count(curr) == 0){
			   mp[curr] = i;   
			}
		}
		return len;
	}
};

