class Solution {
public:
    int longestAwesome(string s) {
        vector<int> pos((1<<10),-1);

        int curr=0,ans=1;
        pos[0]=0;
        for(int i=0;i<s.size();i++)
        {
            char ch=s[i];

            curr^=(1<<(ch-'0'));

            if(pos[curr]!=-1)
            {
                ans=max(ans,i-pos[curr]+1);
            }

            for(int d=0;d<10;d++)
            {
                int req=curr^(1<<d);
                if(pos[req]==-1) continue;
                ans=max(ans,i-pos[req]+1);
            }
            if(pos[curr]==-1) pos[curr]=i+1;
        }

        return ans;
    }
};
