class Solution {
    public int longestAwesome(String s) {
        int n = s.length();
        int[] idx = new int[(int)Math.pow(2, 10)]; // mask can be 2 ^ 10 at most
        // initial state
        Arrays.fill(idx, Integer.MAX_VALUE);
        idx[0] = -1; 
        int mask = 0;
        int ans = 0;
        for (int i = 0; i < n; i++) {
            mask ^= 1 << (s.charAt(i) - '0');
            // check the all even case of palindrome
            ans = Math.max(ans, i - idx[mask]);
            // check the all even but one single case of palindrome
            for (int j = 0; j < 10; j++) {
                ans = Math.max(ans, i - idx[mask ^ (1 << j)]);
            }
            // update the min index ever seen
            idx[mask] = Math.min(idx[mask], i);
        }
        return ans;
    }
}
