class Solution(object):
    def longestAwesome(self, s):

        def compact_string(tempString):
            startPoint = 0

            while startPoint < len(tempString):
                count = 1
                while startPoint+1 < len(tempString) and tempString[startPoint] == tempString[startPoint+1]:
                    tempString.pop(startPoint+1)
                    count += 1

                if count >= 1000:

                    startVal = tempString[startPoint]
                    for i in range(20):
                        tempString.insert(startPoint, startVal + "1")
                        startPoint += 1
                        count -= 1
                    while count > 1000:
                        tempString.insert(startPoint, startVal + "1000")
                        startPoint += 1
                        count -= 1000
                    count -= 20
                    if count % 2 != 0:
                        tempString[startPoint] = tempString[startPoint] + str(count-1)
                        tempString.insert(startPoint+1, tempString[startPoint][0] + str(1))
                        startPoint += 1
                    else:
                        tempString[startPoint] = tempString[startPoint] + str(count)
                    for i in range(20):
                        tempString.insert(startPoint, startVal + "1")
                        startPoint += 1
                else:
                    startVal = tempString[startPoint]
                    tempString[startPoint] = startVal + "1"
                    for i in range(count-1):
                        startPoint += 1
                        tempString.insert(startPoint, startVal + "1")
                startPoint += 1
            return tempString

        s = compact_string(list(s))

        def is_awesome_db(db):
            temp_db = {}
            for k, v in db.iteritems():
                if k[0] not in temp_db:
                    temp_db[k[0]] = 0
                temp_db[k[0]] += int(k[1:]) * v
            size = sum(temp_db.values())
            return len([x for x in temp_db.values() if x % 2 != 0]) < 2, size

        def sliding_window(size):
            db = {}
            for i in range((len(s)-size)+1):
                if len(db) == 0:
                    for v in s[i:i+size]:
                        if v not in db:
                            db[v] = 0
                        db[v] += 1
                else:
                    db[s[i-1]] -= 1
                    if s[i+size-1] not in db:
                        db[s[i+size-1]] = 0
                    db[s[i+size-1]] += 1
                    if db[s[i-1]] == 0:
                        del db[s[i-1]]
                success, string_size = is_awesome_db(db)
                if success:
                    return string_size
            return 0

        for i in range(len(s)):
            string_size = sliding_window(len(s)-i)
            if string_size > 0:
                return string_size
