class Solution {
    public int longestAwesome(String s) {
        Map<String,Integer> map = new HashMap<>();
        int sum = 0;
        int ans = 0;
        map.put("0" , -1);
        for(int j = 0 ; j < s.length() ; j++){
            int t = s.charAt(j) -'0';
            sum ^= (1 << t);

            for(int i = 0 ; i <= 9 ; i++){
                int m = sum ^ (1 << i);
                if(map.containsKey(String.valueOf(m))){
                    ans = Math.max(ans , j - map.get(String.valueOf(m)));
                }
            }
            if(!map.containsKey(sum+"")){
                map.put(sum+"" , j);
            }else{
                ans = Math.max(ans , j - map.get(String.valueOf(sum)));
            }
        }
        return ans;
    }
}
