    public int longestAwesome(String s) {
        int n = s.length(), res = 0, len = (int) Math.pow(2, 10), mask[] = new int[len + 1], curr = 0;
        Arrays.fill(mask, n);
        mask[curr] = -1;  // prefix mask init
        for (int i = 0; i < n; i++) {
            int num = s.charAt(i) - '0';  // switch, even as 0, odd as 1;
            curr ^= 1 << num;
            for (int j = 0; j < 10; j++) res = Math.max(res, i - mask[curr ^ (1 << j)]);  // 212
            res = Math.max(res, i - mask[curr]); // 22
            mask[curr] = Math.min(i, mask[curr]);  // keep the first exists;
        }
        return res;
    }
